import React from "react";
import IMovieTheatre from '../../models/IMovieTheatre';
import Card from 'react-bootstrap/Card';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart } from '@fortawesome/free-solid-svg-icons';



type Props = {
    movie: IMovieTheatre
}
const MoveieListItem = ({ movie }: Props) => {
    const {
        id,
        title,
        year,
        genres,
        ratings,
        poster,
        contentRating,
        duration,
        releaseDate,
        averageRating,
        originalTitle,
        storyline,
        actors,
        imdbRating,
        posterurl
    } = movie;
    return (
        <Card style={{ width: '18rem' }}>
            <Card.Img variant="top" src={`${process.env.REACT_APP_API_BASE_URL}${posterurl}`} />
            {/* <Card.Img variant="top" src="http://localhost:3001/sample-files/MV5BMTg1MTY2MjYzNV5BMl5BanBnXkFtZTgwMTc4NTMwNDI@._V1_SY500_CR0,0,337,500_AL_.jpg" /> */}
            <Card.Body>
                <Card.Title>
                    {title}
                </Card.Title>
                <Card.Text>
                    Add to Favourites
                    <FontAwesomeIcon
                        icon={faHeartRed}
                        className="me-2"
                    />
                </Card.Text>
            </Card.Body>
        </Card>
    );
}

export default MoveieListItem;
