import React, { ReactPropTypes } from "react";
import IMovieTheatre from '../../models/IMovieTheatre';
import ITopRatedIndian from "../../models/ITopRatedIndian";
import Card from 'react-bootstrap/Card';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { text } from "stream/consumers";
import '../common/Heart.css';
import '../../components/common/App.css';
import onHandleFavourite from './onHandleFavourite';
import PropTypes from 'prop-types';





type Props = {
    movie: IMovieTheatre,
    onHandleFavourite: PropTypes.func
    }
const MoveieListItem = ({ movie, onHandleFavourite }: Props) => {
    const {
        id,
        title,
        year,
        genres,
        ratings,
        poster,
        contentRating,
        duration,
        releaseDate,
        averageRating,
        originalTitle,
        storyline,
        actors,
        imdbRating,
        posterurl
    } = movie;

    return (
        <Card style={{ width: '16rem' }}>
            <Card.Img variant="top" src={`${process.env.REACT_APP_API_BASE_URL}${posterurl}`} />
            <Card.Body>
                <Card.Title>
                    {title}
                </Card.Title>
                {/* <svg className="icon icon-heart"><use xlinkHref="#icon-heart"></use></svg> */}
                <Card.Text>
                    Add to Favourites
                    <button type="button" className="Heart">
                    {/* <span className="Heart"> */}
                    <FontAwesomeIcon
                        icon={faHeart}
                        size='lg'
                        className="me-2"
                        onClick={()=> onHandleFavourite(movie)}
                        />
                    {/* </span> */}
                    </button>

                </Card.Text>
            </Card.Body>
        </Card>
    );
}

export default MoveieListItem;
